mapper.update:
update article set
status=#{status} where
articleId=#{articleId}

mapper.select:
select *
from article
where
firstFolderId=#{firstFolderId}
 and secondFolderId=#{secondFolderId}
and thirdFolderId=#{thirdFolderId}
and fourthFolderId=#{fourthFolderId}
and status = #{status}
order by
createTime desc limit #{offset},#{rows}

select
count(*)
from article
where
firstFolderId=#{firstFolderId}
and secondFolderId=#{secondFolderId}
and thirdFolderId=#{thirdFolderId}
and fourthFolderId=#{fourthFolderId}
and status = #{status}

select
count(*)
from article
where
firstFolderId=#{firstFolderId}
and secondFolderId=#{secondFolderId}
and thirdFolderId=#{thirdFolderId}
 and fourthFolderId=#{fourthFolderId}

select * from
article
where status=#{status}
and firstFolderId=#{firstFolderId}
and secondFolderId=#{secondFolderId}
and thirdFolderId=#{thirdFolderId}
 and fourthFolderId=#{fourthFolderId}
order by createTime
desc limit
#{offset},#{rows}

select
count(*)
from article where status=#{status}
and firstFolderId=#{firstFolderId}
and secondFolderId=#{secondFolderId}
and thirdFolderId=#{thirdFolderId}
 and fourthFolderId=#{fourthFolderId}
